DROP TABLE IF EXISTS `employment_contracts`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!50503 SET character_set_client = utf8mb4 */;
CREATE TABLE `employment_contracts` (
  `id` bigint NOT NULL AUTO_INCREMENT,
  `user_id` bigint NOT NULL,
  `contract_no` varchar(50) DEFAULT NULL,
  `contract_type` varchar(32) DEFAULT NULL,
  `start_date` date NOT NULL,
  `end_date` date DEFAULT NULL,
  `base_salary` decimal(18,2) NOT NULL,
  `currency` varchar(8) DEFAULT NULL,
  `status` varchar(24) DEFAULT NULL COMMENT 'Contract state: draft, active, expired, terminated',
  `file_path` varchar(1024) DEFAULT NULL,
  `note` varchar(255) DEFAULT NULL,
  `created_by_account_id` bigint DEFAULT NULL,
  `created_at` datetime NOT NULL DEFAULT (utc_timestamp()),
  `updated_at` datetime NOT NULL DEFAULT (utc_timestamp()),
  `approved_by_account_id` bigint DEFAULT NULL COMMENT 'User ID (not account ID) of the person who approved/rejected the contract',
  `approved_at` datetime DEFAULT NULL COMMENT 'Timestamp when contract was approved',
  `rejected_reason` text COMMENT 'Reason for rejection if status is rejected',
  `approval_status` varchar(20) DEFAULT 'pending' COMMENT 'Approval workflow status: pending, approved, rejected',
  PRIMARY KEY (`id`),
  KEY `IX_contract_user_start` (`user_id`,`start_date`),
  KEY `FK_contract_creator` (`created_by_account_id`),
  KEY `idx_contract_status` (`status`),
  KEY `idx_contract_approval_status` (`approval_status`),
  KEY `idx_contract_approved_by` (`approved_by_account_id`),
  KEY `idx_employment_contracts_user_id` (`user_id`),
  KEY `idx_employment_contracts_created_at` (`created_at`),
  KEY `idx_employment_contracts_dates` (`start_date`,`end_date`),
  CONSTRAINT `fk_contract_approved_by` FOREIGN KEY (`approved_by_account_id`) REFERENCES `users` (`id`) ON DELETE SET NULL,
  CONSTRAINT `FK_contract_creator` FOREIGN KEY (`created_by_account_id`) REFERENCES `accounts` (`id`),
  CONSTRAINT `FK_contract_user` FOREIGN KEY (`user_id`) REFERENCES `users` (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=75 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci;
